import React, { useReducer } from 'react'
import Nontification from '../Login-nontification/Login-nontificaton'
import Reducer from '../../Reducer/Login'
import InitialState from '../../InitiaState/Login'
// import { Link } from 'react-router-dom'
import ReducerError from '../../Reducer/Error'
import Error from '../../InitiaState/Error'
import { GET_LIST_URL_FOR_LOGIN } from '../../Config/index'

const axios = require('axios')
const LoginForm = () => {
	const [state, dispatch] = useReducer(Reducer, InitialState)
	const [errorState, dispatchError] = useReducer(ReducerError, Error)
	const onSubmitForm = (e) => {
		e.preventDefault()
		const { name, room, password } = state
		const error = []
		let isError = false
		//check if( any thing empty)
		if (name === '') {
			error.push("Name can't be empty")
			isError = true
		}
		if (room === '') {
			error.push("Room can't be empty")
			isError = true
		}
		if (password === '') {
			error.push("Password can't be empty")
			isError = true
		}
		//if room is exist
		console.log(error)
		let listRoom = null
		;(async () => {
			await axios
				.get(GET_LIST_URL_FOR_LOGIN)
				.then((res) => {
					listRoom = res
				})
				.catch((error) => {
					console.log(error)
				})
		})();
		console.log(listRoom)
	}
	return (
		<div class='content'>
			<h1>Login</h1>
			<hr />
			<form action=''>
				<label htmlFor='name'>Name</label>
				<input
					type='text'
					name='name'
					id='name'
					placeholder='Enter your name'
					value={state.name}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE NAME',
							payload: e.target.value,
						})
					}
				/>
				<label htmlFor='room'>Room</label>
				<input
					type='text'
					name='room'
					id='room'
					placeholder='Enter room'
					value={state.room}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE ROOM',
							payload: e.target.value,
						})
					}
				/>
				<label htmlFor='password'>Password</label>
				<input
					type='password'
					name='password'
					id='password'
					placeholder='Enter password'
					value={state.password}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE PASSWORD',
							payload: e.target.value,
						})
					}
				/>
				{/* <Link
					onClick={(e) =>
						state.name && state.password && state.room
							? null
							: e.preventDefault()
					}
					to={`/chat`}
				> */}
				<button class='btn-sign-in' onClick={(e) => onSubmitForm(e)}>
					GO
				</button>
				{/* </Link> */}
				<Nontification />
			</form>
		</div>
	)
}

export default LoginForm
