	const { message, username } = props
	const checkTime = () => {
		const { date } = message
		var d = new Date().getTime()
		if (d - date < 60) return 'Now'
		if (d - date > 60 && d - date < 60 * 60) {
			return `${(d - date) / 60} minutes ago`
		}
		if (d - date > 60 * 60 && d - date < 60 * 60 * 24) {
			return `${(d - date) / (60 * 60)} hours ago`
		}
		return new Date().toISOString().slice(0, 10)
	}
