import React, { useState } from 'react'
import { Link, Redirect, useHistory } from 'react-router-dom'
import { fetchPOST } from './../../Api/index'
import { CREATE_ROOM } from '../../Config/index'

const LoginRegister = () => {
	const [room, setRoom] = useState('')
	const [password, setPassword] = useState('')
	const [repassword, setRepassword] = useState('')
	const [roomError, setRoomError] = useState(['', false])
	const [passwordError, setPasswordError] = useState(['', false])
	const [repasswordError, setRepasswordError] = useState(['', false])

	const history = useHistory()
	console.log(history)

	const handleChange = (e) => {
		if (e.target.name === 'room') {
			setRoom(e.target.value)
			setRoomError(['', false])
		}
		if (e.target.name === 'password') {
			setPassword(e.target.value)
			setPasswordError(['', false])
		}
		if (e.target.name === 'repassword') {
			setRepassword(e.target.value)
			setRepasswordError(['', false])
		}
	}
	const handleSubmit = async (e) => {
		e.preventDefault()
		setRoomError(['', false])
		setRepasswordError(['', false])
		setPasswordError(['', false])
		if (repassword !== password) {
			setRepasswordError([
				'Re Password must match with upon password!',
				true,
			])
			return
		}
		await fetchPOST({ room, password }, CREATE_ROOM)
			.then((res) => res.json())
			.then(
				(data) => {
					if (data.res) {
						history.replace('/room')
					}
					if (data.err) {
						setRoomError([
							'Some error happens ,I dont know !!!',
							true,
						])
						return
					}
					if (data.taken) {
						setRoomError(['Room name has been taken !', true])
						return
					}
				},
				(error) => console.log(error),
			)
	}
	return (
		<>
			<div className='limiter'>
				<div className='container-login100'>
					<div className='wrap-login100 p-l-55 p-r-55 p-t-65 p-b-54'>
						<form className='login100-form validate-form'>
							<span className='login100-form-title p-b-49'>
								{' '}
								CREATE YOUR OWN ROOM{' '}
							</span>

							<div
								className={
									roomError[0] !== ''
										? 'wrap-input100 validate-input m-b-23 alert-validate'
										: 'wrap-input100 validate-input m-b-23'
								}
								data-validate={roomError[0]}
							>
								<span className='label-input100'>
									Room name
								</span>
								<input
									className={
										room !== ''
											? roomError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: roomError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='text'
									name='room'
									placeholder='Type your room name'
									value={room}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf206;'
								></span>
							</div>

							<div
								className={
									passwordError[0] !== ''
										? 'wrap-input100 validate-input alert-validate'
										: 'wrap-input100 validate-input'
								}
								data-validate={passwordError[0]}
							>
								<span className='label-input100'>Password</span>
								<input
									className={
										password !== ''
											? passwordError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: passwordError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='password'
									name='password'
									placeholder='Type your password'
									value={password}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf190;'
								></span>
							</div>

							<div
								className={
									repasswordError[0] !== ''
										? 'wrap-input100 validate-input alert-validate'
										: 'wrap-input100 validate-input'
								}
								data-validate={repasswordError[0]}
							>
								<span className='label-input100'>
									Re-Password
								</span>
								<input
									className={
										repassword !== ''
											? repasswordError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: repasswordError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='password'
									name='repassword'
									placeholder='Re enter password'
									value={repassword}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf190;'
								></span>
							</div>

							<div className='container-login100-form-btn'>
								<div className='wrap-login100-form-btn'>
									<div className='login100-form-bgbtn'></div>
									<button
										className='login100-form-btn'
										onClick={(e) => handleSubmit(e)}
									>
										CREATE NOW!
									</button>
								</div>
							</div>

							<div class='flex-col-c p-t-155'>
								<Link to='/login' className='txt2'>
									Back to Login
								</Link>
							</div>
						</form>
					</div>
				</div>
			</div>

			<div id='dropDownSelect1'></div>
		</>
	)
}

export default LoginRegister
