import React, { useReducer, useState } from 'react'
import Nontification from '../Login-nontification/Login-nontificaton'
import Reducer from '../../Reducer/Login'
import InitialState from '../../InitiaState/Login'
// import { Link } from 'react-router-dom'
import { CHECK_ROOM, CREATE_ROOM } from '../../Config/index'
import { useHistory } from 'react-router-dom'
import { fetchPOST } from '../../Api/index'

const LoginForm = (props) => {
	let history = useHistory()
	const { handleLogout } = props
	const [state, dispatch] = useReducer(Reducer, InitialState)
	const [error, seterror] = useState([])
	const handleLogin = async (e) => {
		e.preventDefault()
		let { room, password } = state
		let error = []

		if (room === '') {
			error.push("Room can't be empty")
		}
		if (password === '') {
			error.push("Password can't be empty")
		}
		if (error.length === 0) {
			await fetchPOST({ room, password }, CHECK_ROOM)
				.then((res) => res.json())
				.then(
					(rs) => {
						if (rs.res) {
							localStorage.setItem('room', room)
							history.push(`/chat/${room}`)
						}
						if (rs.err) {
							error.push('Room or Password was wrong!!!')
							seterror(error)
							return
						}
					},
					(error) => {
						console.log(error)
					},
				)
		} else {
			seterror(error)
		}
	}
	const handleCreate = async (e) => {
		e.preventDefault()
		let { room, password } = state
		let error = []
		if (room === '') {
			error.push("Room can't be empty")
		}
		if (password === '') {
			error.push("Password can't be empty")
		}
		if (room && password) {
			await fetchPOST({ room, password }, CREATE_ROOM)
				.then((res) => res.json())
				.then(
					(rs) => {
						if (rs.res) {
							localStorage.setItem('room', room)
							history.push(`/chat/${room}`)
						}
						if (rs.err || rs.taken) {
							error.push('Room or Password was wrong!!!')
							seterror(error)
							return
						}
					},
					(error) => {
						console.log(error)
					},
				)
		}
		seterror(error)
		return error
	}
	return (
		<div className='content'>
			<div className='welcome'>
				<h1>Welcome</h1>
				<p className='username'>{localStorage.getItem('username')}</p>
				<button
					className='btn-logout'
					onClick={(e) => {
						e.preventDefault()
						localStorage.removeItem('username')
						handleLogout()
					}}
				>
					Logout
				</button>
			</div>

			<h1>Choose Room</h1>

			<hr />
			<form action=''>
				<label htmlFor='room'>Room</label>
				<input
					type='text'
					name='room'
					id='room'
					placeholder='Enter room'
					value={state.room}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE ROOM',
							payload: e.target.value,
						})
					}
				/>
				<label htmlFor='password'>Password</label>
				<input
					type='password'
					name='password'
					id='password'
					placeholder='Enter password'
					value={state.password}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE PASSWORD',
							payload: e.target.value,
						})
					}
				/>
				<div className='control-btn'>
					<button
						className='btn-sign-in'
						onClick={(e) => handleLogin(e)}
					>
						GO
					</button>
					<button
						className='btn-sign-in'
						onClick={(e) => handleCreate(e)}
					>
						CREATE
					</button>
				</div>

				{/* </Link> */}
				<Nontification error={error} />
			</form>
		</div>
	)
}

export default LoginForm
