import React, { useEffect, useReducer, useState } from 'react'
import Nontification from '../Login-nontification/Login-nontificaton'
import Reducer from '../../Reducer/LoginUser'
import InitialState from '../../InitiaState/LoginUser'
import { CHECK_USER_LOGIN, CREATE_NEW_USER } from '../../Config/index'
const LoginForm = (props) => {
	const { handleLogin } = props
	const [state, dispatch] = useReducer(Reducer, InitialState)
	const [error, seterror] = useState([])
	const { username, usernamepass } = state
	const handleSignIn = async (e) => {
		e.preventDefault()
		let listError = []
		// let users = null
		if (!username || !usernamepass) {
			listError.push('User name or Password can be blank')
			seterror(listError)
			return
		}
		if (username.length <= 6 || usernamepass.length <= 6) {
			listError.push(
				'User Name or Password must be least than 6 characters',
			)
			seterror(listError)
			return
		}
		await fetch(CHECK_USER_LOGIN, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				username,
				password: usernamepass,
			}),
		})
			.then((res) => res.json())
			.then(
				(rs) => {
					if (rs.res) {
						localStorage.setItem('username', username)
						handleLogin()
						return
					}
					if (rs.err) {
						listError.push('?')
						seterror(listError)
						return
					}
				},
				(error) => {
					console.log(error)
				},
			)
	}
	const handleSignUp = async (e) => {
		e.preventDefault()
		let listError = []
		if (!username || !usernamepass) {
			listError.push('User name or Password can be blank')
			seterror(listError)
			return
		}
		if (username.length <= 6 || usernamepass.length <= 6) {
			listError.push(
				'User Name or Password must be least than 6 characters',
			)
			seterror(listError)
			return
		}
		await fetch(CREATE_NEW_USER, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify({
				username,
				password: usernamepass,
			}),
		})
			.then((res) => res.json())
			.then(
				(data) => {
					if (data.res) {
						console.log(data.res)
						localStorage.setItem('username', username)
						handleLogin()
						return
					}
					if (data.err) {
						console.log(data.err)
						return
					}
					if (data.taken) {
						listError.push(
							'Account is already existed , try another!',
						)
						seterror(listError)
					}
				},
				(error) => console.log(error),
			)
	}
	return (
		<div className='content'>
			<h1>Login</h1>
			<hr />
			<form action=''>
				<label htmlFor='username'>User Name</label>
				<input
					type='text'
					name='username'
					id='username'
					placeholder='Enter user name'
					value={state.username}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE USER NAME',
							payload: e.target.value,
						})
					}
				/>
				<label htmlFor='usernamepass'>Password</label>
				<input
					type='password'
					name='usernamepass'
					id='usernamepass'
					placeholder='Enter password'
					value={state.usernamepass}
					onChange={(e) =>
						dispatch({
							type: 'UPDATE PASSWORD FOR USER NAME',
							payload: e.target.value,
						})
					}
				/>
				<div classNameName='control-btn'>
					<button
						className='btn-sign-in'
						onClick={(e) => handleSignIn(e)}
					>
						SIGN IN
					</button>
					{/* <h1>OR</h1> */}
					<button
						className='btn-sign-in'
						onClick={(e) => handleSignUp(e)}
					>
						SIGN UP
					</button>
				</div>

				{/* </Link> */}
			</form>
			<Nontification error={error} />
		</div>
	)
}

export default LoginForm
