import React from 'react'

const index = () => {
	const [msg, setMsg] = useState('')
	const handleChange  = (e) =>{
		e.preventDefault();

	}
	return (
		<div class='textmes-btn'>
			<form action=''>
				<input
					type='text'
					class='textmes'
					placeholder='Your message here ...'
					name='msg'
					value={msg}
					id='msg'
					onChange = {(e)=> handleChange(e)}
				/>
				<button class='btn-enter'>Send</button>
			</form>
		</div>
	)
}

export default index
