import React, { useEffect, useState } from 'react'

const Index = (props) => {
	const { message, username } = props
	const [sentTime, setSentTime] = useState('now')
	console.log(message)
	const checkTime = () => {
		let [day, month, year] = new Date()
			.toLocaleDateString('en-GB')
			.split('/')
		let [hour, minute, second] = new Date()
			.toLocaleTimeString('en-GB')
			.split(/:| /)
		let { date } = message
		if (
			parseInt(date.y) === parseInt(year) &&
			parseInt(date.mo) === parseInt(month) &&
			parseInt(date.d) === parseInt(day)
		) {
			console.log('trung ngay thang')
			if (parseInt(date.h) !== parseInt(hour)) {
				console.log('khac gio')
				setSentTime(`${hour - parseInt(date.h)} hours ago`)
			}
			if (parseInt(date.mi) !== minute) {
				console.log('khac phut')
				setSentTime(`${minute - parseInt(date.mi)} minutes ago`)
				return
			} else {
				console.log('khac giay')
				setSentTime('now')
				return
			}
		}
		return `${date.h}:${date.mi}:${date.s} ${date.d}/${date.mo}/${date.y}`
	}
	useEffect(() => {
		const { date } = message
		console.log([date.d, date.mo, date.y, date.h, date.mi, date.s])
		setInterval(checkTime, 1000)
		return () => {
			clearInterval()
		}
	}, [])
	return (
		<>
			{message.username === username ? (
				<li class='your-mes message'>
					<div class='content'>
						<p class='text'>{message.text}</p>
						<h4 class='name'>
							{message.username}
							<p class='your-text-date'>Sent : {sentTime} </p>
						</h4>
					</div>
				</li>
			) : (
				<li class='another-person-mes message'>
					<div class='content'>
						<h4 class='name'>
							{message.username}
							<p class='another-text-date'>Sent : {sentTime}</p>
						</h4>
						<p class='text'>{message.text}</p>
					</div>
				</li>
			)}
		</>
	)
}

export default Index
