import React, { useEffect, useState } from 'react'

const Index = (props) => {
	const { message, username } = props
	const [sentTime, setSentTime] = useState('now')
	const checkTime = () => {
		let [month, day, year] = new Date()
			.toLocaleDateString('en-US')
			.split('/')
		let [hour, minute, second] = new Date()
			.toLocaleTimeString('en-US')
			.split(/:| /)
		let { date } = message
		console.log([month, day, year, hour, minute, second])
		if (date.y === year && date.mo === month && date.d === day) {
			console.log('trung ngay thang nam')
			if (date.h !== hour) {
				console.log('khac gio')
				setSentTime(`${hour - date.h} hours ago`)
			}
			if (date.m !== minute) {
				console.log('khac phut')
				setSentTime(`${minute - date.mi} minutes ago`)
				return
			} else {
				console.log('do nothing')
				setSentTime('now')
				return
			}
		}
	}
	useEffect(() => {
		console.log(message.date)
		setInterval(checkTime, 1000)
		return () => {
			clearInterval()
		}
	}, [])
	return (
		<>
			{message.username === username ? (
				<li class='your-mes message'>
					<div class='content'>
						<p class='text'>{message.text}</p>
						<h4 class='name'>
							{username}
							<p class='your-text-date'>Sent : {sentTime} </p>
						</h4>
					</div>
				</li>
			) : (
				<li class='another-person-mes message'>
					<div class='content'>
						<h4 class='name'>
							{username}
							<p class='another-text-date'>
								Sent : {setInterval(checkTime, 1000)}
							</p>
						</h4>
						<p class='text'>{message.text}</p>
					</div>
				</li>
			)}
		</>
	)
}

export default Index
