import React, { useEffect, useState } from 'react'

const index = (props) => {
	const { message, username } = props
	const [sentTime, setSentTime] = useState('')
	const checkTime = () => {
		let [month, day, year] = new Date()
			.toLocaleDateString('en-US')
			.split('/')
		let [hour, minute, second] = new Date()
			.toLocaleTimeString('en-US')
			.split(/:| /)
		let { date } = message
		if (date.y === year && date.m === month && date.d === day) {
			if(date.h === hour){
				setSentTime(``)
			}
			if (date.m === minute) {
				setSentTime(minute - date.m)
				return
			}
			else{
				setSentTime(0)
				return
			}
		}
	}
	useEffect(() => {
		setInterval(checkTime, 1000)
		return () => {
			clearInterval()
		}
	}, [])
	return (
		<>
			{message.username === username ? (
				<li class='your-mes message'>
					<div class='content'>
						<p class='text'>{message.text}</p>
						<h4 class='name'>
							{username}
							<p class='your-text-date'>Sent : {sentTime} </p>
						</h4>
					</div>
				</li>
			) : (
				<li class='another-person-mes message'>
					<div class='content'>
						<h4 class='name'>
							{username}
							<p class='another-text-date'>
								Sent : {setInterval(checkTime, 1000)}
							</p>
						</h4>
						<p class='text'>{message.text}</p>
					</div>
				</li>
			)}
		</>
	)
}

export default index
