import React, { useEffect, useState } from 'react'
import io from 'socket.io-client'
import { GET_URL_FOR_SOCKET } from '../../Config/index'
let socket
const Index = (props) => {
	const { room } = props
	const [participant, setparticipant] = useState(0)
	useEffect(() => {
		socket = io('http://localhost:9999')
		socket.on('getParticipant' , p =>{
			if(p===null){
				setparticipant(1)
				socket.emit('setParticipant' , 1)
			}
			else{
				setparticipant(p++)
				socket.emit('setParticipant')
			}
		})
	})

	return (
		<div class='status-room'>
			<div class='room-name'>
				<p>Room name : {room}</p>
			</div>
			<div class='participant'>
				<p>Number Participant : 20</p>
			</div>
			<div class='button-close'>
				<i class='fas fa-times-circle close-btn'></i>
			</div>
		</div>
	)
}

export default Index
