import React, { useState } from 'react'

const Login = () => {
	const [username, setUsername] = useState('')
	const [password, setPassword] = useState('')
	const [usernameBlur, setUsernameBlur] = useState(false)
	const [passwordBlur, setPasswordBlur] = useState(false)
	const [usernameError, setUsernameError] = useState('')
	const [passwordError, setpasswordError] = useState('')
	const [isuserError, setIsuserError] = useState(false)
	const [ispasswordError, setIspassworddError] = useState(false)
	const onBlurInput = () => {
		if (username === '') setUsernameBlur(false)
		if (username.trim !== '') setUsernameBlur(true)

		if (password === '') setPasswordBlur(false)
		if (password.trim !== '') setPasswordBlur(true)
	}
	const handleChange = (e) => {
		if (e.target.name === 'username') setUsername(e.target.value)
		if (e.target.name === 'password') setPassword(e.target.value)
	}
	const handleSubmit = (e) => {
        e.preventDefault()
        setIsuserError(true)
        setIspassworddError(true)
        setUser
	}
	return (
		<>
			<div className='limiter'>
				<div className='container-login100'>
					<div className='wrap-login100 p-l-55 p-r-55 p-t-65 p-b-54'>
						<form className='login100-form validate-form'>
							<span className='login100-form-title p-b-49'>
								{' '}
								Login{' '}
							</span>

							<div className='wrap-input100 validate-input m-b-23'>
								<span className='label-input100'>Username</span>
								<input
									className={
										usernameBlur === true
											? 'input100 has-val'
											: 'input100'
									}
									type='text'
									name='username'
									placeholder='Type your username'
									value={username}
									onBlur={() => onBlurInput()}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf206;'
								></span>
							</div>

							<div className='wrap-input100 validate-input'>
								<span className='label-input100'>Password</span>
								<input
									className={
										passwordBlur === true
											? 'input100 has-val'
											: 'input100'
									}
									type='password'
									name='password'
									placeholder='Type your password'
									value={password}
									onBlur={() => onBlurInput()}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf190;'
								></span>
							</div>

							<div className='container-login100-form-btn'>
								<div className='wrap-login100-form-btn'>
									<div className='login100-form-bgbtn'></div>
									<button
										className='login100-form-btn'
										onClick={(e) => handleSubmit(e)}
									>
										Login
									</button>
								</div>
							</div>

							<div className='flex-col-c p-t-155'>
								<span className='txt1 p-b-17'>
									{' '}
									Or Sign Up Using{' '}
								</span>

								<a href='#' className='txt2'>
									{' '}
									Sign Up{' '}
								</a>
							</div>
						</form>
					</div>
				</div>
			</div>

			<div id='dropDownSelect1'></div>
		</>
	)
}

export default Login
