import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { fetchPOST } from './../../Api/index'
import {} from '../../Config/index'

const LoginRegister = () => {
	const [username, setUsername] = useState('')
	const [password, setPassword] = useState('')
	const [repassword, setRepassword] = useState('')
	const [usernameError, setUsernameError] = useState(['', false])
	const [passwordError, setPasswordError] = useState(['', false])
	const [repasswordError, setRepasswordError] = useState(['', false])

	const handleChange = (e) => {
		if (e.target.name === 'username') {
			setUsername(e.target.value)
			setUsernameError(['', false])
		}
		if (e.target.name === 'password') {
			setPassword(e.target.value)
			setPasswordError(['', false])
		}
		if (e.target.name === 'repassword') {
			setRepassword(e.target.value)
			setRepasswordError(['', false])
		}
	}
	const handleSubmit = async (e) => {
		e.preventDefault()
		setUsernameError(['', false])
		setRepasswordError(['', false])
		setPasswordError(['', false])
		if (username.length < 6)
			setUsernameError(['At least 6 charaters', true])
		if (password.length < 6)
			setPasswordError(['At least 6 charaters', true])
		if (username.length < 6 || password.length < 6) {
			return
		}
		if (repassword !== password) {
			setRepasswordError([
				'Re Password must match with upon password!',
				true,
			])
			return
		}
		await fetchPOST({ username, password }, CREATE_NEW_USER)
			.then((res) => res.json())
			.then(
				(data) => {
					if (data.res) {
						// console.log(data.res)
						// localStorage.setItem('username', username)
						// handleLogin()
						return
					}
					if (data.err) {
						console.log(data.err)
						return
					}
					if (data.taken) {
						// listError.push(
						// 	'Account is already existed , try another!',
						// )
						// seterror(listError)
					}
				},
				(error) => console.log(error),
			)
	}
	return (
		<>
			<div className='limiter'>
				<div className='container-login100'>
					<div className='wrap-login100 p-l-55 p-r-55 p-t-65 p-b-54'>
						<form className='login100-form validate-form'>
							<span className='login100-form-title p-b-49'>
								{' '}
								Register{' '}
							</span>

							<div
								className={
									usernameError[0] !== ''
										? 'wrap-input100 validate-input m-b-23 alert-validate'
										: 'wrap-input100 validate-input m-b-23'
								}
								data-validate={usernameError[0]}
							>
								<span className='label-input100'>Username</span>
								<input
									className={
										username !== ''
											? usernameError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: usernameError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='text'
									name='username'
									placeholder='Type your username'
									value={username}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf206;'
								></span>
							</div>

							<div
								className={
									passwordError[0] !== ''
										? 'wrap-input100 validate-input alert-validate'
										: 'wrap-input100 validate-input'
								}
								data-validate={passwordError[0]}
							>
								<span className='label-input100'>Password</span>
								<input
									className={
										password !== ''
											? passwordError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: passwordError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='password'
									name='password'
									placeholder='Type your password'
									value={password}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf190;'
								></span>
							</div>

							<div
								className={
									repasswordError[0] !== ''
										? 'wrap-input100 validate-input alert-validate'
										: 'wrap-input100 validate-input'
								}
								data-validate={repasswordError[0]}
							>
								<span className='label-input100'>
									Re-Password
								</span>
								<input
									className={
										repassword !== ''
											? repasswordError[1] === true
												? 'alert-validate input100 has-val'
												: 'input100 has-val'
											: repasswordError[1] === true
											? 'alert-validate input100 '
											: 'input100'
									}
									type='password'
									name='repassword'
									placeholder='Re enter password'
									value={repassword}
									onChange={(e) => handleChange(e)}
								/>
								<span
									className='focus-input100'
									data-symbol='&#xf190;'
								></span>
							</div>

							<div className='container-login100-form-btn'>
								<div className='wrap-login100-form-btn'>
									<div className='login100-form-bgbtn'></div>
									<button
										className='login100-form-btn'
										onClick={(e) => handleSubmit(e)}
									>
										Sign Up
									</button>
								</div>
							</div>

							<div class='flex-col-c p-t-155'>
								<Link to='/login' className='txt2'>
									Back to Login
								</Link>
							</div>
						</form>
					</div>
				</div>
			</div>

			<div id='dropDownSelect1'></div>
		</>
	)
}

export default LoginRegister
