import React from 'react'

const index = (props) => {
	const { message, username } = props
	const checkTime = () =>{
		const {date} =message
		var d = new Date().getTime()
		if(d - date < 60) return "Now"
		if(d - date  > 60 && d - date < 60*60){
			return `${(d-date)/60} minutes ago`
		}
		if(d-date > 60*60 && d- date < 60*60*24){
			return ` hours age`
		}
	}
	return (
		<>
			{message.username === username ? (
				<li class='your-mes message'>
					<div class='content'>
						<p class='text'>{message.text}</p>
						<h4 class='name'>
							{username}
							<p class='your-text-date'>
								Sent : 21:00:00 17-12-2020
							</p>
						</h4>
					</div>
				</li>
			) : (
				<li class='another-person-mes message'>
					<div class='content'>
						<h4 class='name'>
							{username}
							<p class='another-text-date'>
								Sent : 21:00:00 17-12-2020
							</p>
						</h4>
						<p class='text'>{message.text}</p>
					</div>
				</li>
			)}
		</>
	)
}

export default index
